import React from 'react'
import { useState } from 'react';



const Product = (props) => {

    const [quantity, setQuantity] = useState(0);


    const addOne = (count) => {
        setQuantity(count + 1);
    }
    const subOne = (count) => {
        if (count - 1 > 0) {
            setQuantity(count - 1);
        }
    }
    return (

        <div className="meal" key={props.id}>
            <h3>{props.name}</h3>
            <p>{props.price}</p>
            <p>miqdar: {quantity} <span onClick={() => addOne(quantity)}>+</span> <span onClick={() => subOne(quantity)}>-</span></p>
        </div>
    )
}

export default Product
