import React from 'react'
import { useEffect, useState } from 'react';
import Axios from 'axios';

const Form = (props) => {
    const [table, setTable] = useState("");
    const [server, setServer] = useState("");
    const [meal, setMeal] = useState(null);
    const [choosenMeals, setChoosenMeals] = useState([]);
    const [quantity, setQuantity] = useState(0);
    const [price, setPrice] = useState(0);
    const [cost, setCost] = useState(0);
    const [status, setStatus] = useState("verildi");
    const [orders, setOrders] = useState({});


    const [totalCost, setTotalCost] = useState(0);

    const startOrder = (e) => {
        e.preventDefault();

    }

    const handleCost = (e) => {
        setQuantity(e.target.value);

        props.meals.map((item) => {
            if (item.name === meal) {
                setPrice(item.price);
            }
        })
        setCost((e.target.value) * price);
    }
    console.log("Cost " + cost);
    console.log(price);

    const getMeal = (e) => {
        setMeal(e.target.value);
        props.meals.map((item) => {
            if (item.name === meal) {
                setPrice(item.price);
            }
        })
        setQuantity(0);
    }
    console.log("Price: " + price);
    const addMeal = (e) => {
        e.preventDefault();
        setCost(quantity * price);
        //choosenMeals.push(meal);
        setChoosenMeals([...choosenMeals, { id: Math.random() * 1000, name: meal, amount: quantity, cost: cost, status: status }]);
        setMeal(null);
        setQuantity(0);
        setCost(0);
    }

    const handleCancel = (e, id) => {
        e.preventDefault();
        //const newList = choosenMeals.filter((meal)=> meal.id !== index)
        setChoosenMeals((meals) => meals.filter((meal) => meal.id !== id));
    }

    const submitOrder = (e) => {
        e.preventDefault();

        setOrders({ meals: choosenMeals, server: server, totalCost: choosenMeals.reduce((a, v) => a = a + v.cost, 0), status: "sonlanmayib", table: table });


        Axios.post("http://localhost:8000/orders", {
            orders,
        }).then(console.log("Success")).catch(err => console.log(err));
        setChoosenMeals([]);
    }
    console.log(choosenMeals);
    //console.log("orders :  "+orders[0]);

    return (
        <div className='form'>
            <form>
                <div className='table' >
                    <label htmlFor="table">Masa</label>
                    <select name="table" id="table" onChange={(e) => setTable(e.target.value)}>
                        {props.tables.map((table) => {
                            return (
                                <option key={table.id} value={table.id}>{table.id}</option>
                            )
                        })}
                    </select>
                </div>
                <div className='server'>
                    <label htmlFor="server">Xidmetci</label>
                    <select name="server" id="server" onChange={(e) => setServer(e.target.value)}>
                        {props.servers.map((server) => {
                            return (
                                <option key={server.id} value={server.name + " " + server.surname}>{server.name + " " + server.surname}</option>
                            )
                        })}
                    </select>
                </div>
                <br />
                <button onClick={startOrder} className="start-btn">Sifarisi baslat</button>
                <br />
                <label htmlFor="meal">Mehsul Adi</label>
                <select name="meal" id="meal" onChange={getMeal}>
                    <option value="Sec" disabled selected>Sec</option>
                    {props.meals.map((meal) => {
                        return (
                            <option value={meal.name} key={meal.id}>{meal.name}</option>
                        )
                    })}
                </select>

                <label htmlFor="amount">Miqdar</label>
                <input type="number" id='amount' name='amount' min="0" value={quantity} onChange={handleCost} />
                <p>Qiymet: {quantity * price}</p>
                <button onClick={addMeal}>elave et</button>




                <button onClick={submitOrder}>Sifarisi Sonuclandir</button>
            </form>


            <div className="order">
                <table>
                    <tr>
                        <th>Mehsul Adi</th>
                        <th>Miqdar</th>
                        <th>Mebleg</th>
                        <th>Status</th>
                        <th>Geri</th>
                    </tr>
                    {choosenMeals.map((meal, index) => {
                        //console.log("cem: "+choosenMeals.reduce((a,v)=> a = a+v.cost,0));
                        //setTotalCost(choosenMeals.reduce((a,v)=> a = a+v.cost,0));
                        return (
                            <tr>
                                <td>{meal.name}</td>
                                <td>{meal.amount}</td>
                                <td>{meal.cost}</td>
                                <td>{meal.status}</td>
                                <td><button onClick={(e) => handleCancel(e, meal.id)}>geri</button></td>
                            </tr>
                        )
                    })}
                </table>
                {/* <h1>Cemi mebleg: {choosenMeals.reduce((a,v)=>{a = a+v.cost},0)}</h1> */}
                <h1>Cemi mebleg: {choosenMeals.reduce((a, v) => a = a + v.cost, 0)}</h1>
            </div>
        </div >
    )
}

export default Form
