import React from 'react'
import { useEffect, useState } from 'react';

const Form = (props) => {
    const [cost, setCost] = useState(null);
    const [count, setCount] = useState(0);

    const handleSubmit = () => {

    }


    const defineProduct = (e)=>{
        e.preventDefault();
        props.meals.forEach((meal)=>{
            if(meal.name === e.target.value){
                setCost(meal.price);
            }
        })
    }


    const defineCost=(e)=>{
        e.preventDefault();
        setCount(e.target.value);
        props.setPrice(e.target.value * cost)
    }

    useEffect(() => {
        const fetchData = async () => {
            const responseServers = await fetch("http://localhost:8000/servers");
            const serversList = await responseServers.json();
            props.setServers(serversList);

            const responseTables = await fetch("http://localhost:8000/tables");
            const tablesList = await responseTables.json();
            props.setTables(tablesList);
        };

        fetchData();
    }, []);
    return (
        <div>
            <h1>Create Order</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="masa">Masa</label>
                <select name="masa" id="masa">
                    <option value="Sec" selected disabled>sec</option>
                    {props.tables.map((table) => {
                        return (
                            <option key={table.id} value={table.id}>{table.id}</option>
                        )
                    })}
                </select>
                <label htmlFor="server">Xidmətçi</label>
                <select name="server" id="server">
                    <option value="Sec" selected disabled>sec</option>
                    {props.servers.map((server) => {
                        return (
                            <option key={server.id} value={server.name + ' ' + server.surname}>{server.name + ' ' + server.surname}</option>
                        )
                    })}
                </select>
                <input type="button" value="Sifarisi Baslat" />
            </form>
            <form>
                <label htmlFor="product-name">Mehsul Adi</label>
                <select name="product-name" id="product-name" onChange={(e)=> defineProduct(e)}>
                    <option value="Sec" selected disabled>Sec</option>
                    {props.meals.map((meal)=>{
                        return(
                            <option key={meal.id} value={meal.name}>{meal.name}</option>
                        )
                    })}
                </select>
                <label htmlFor="quantity">Miqdar</label>
                <input type="number" name='quantity' id='quantity' min="0" onChange={(e)=> defineCost(e)} />
                <h5>Qiymeti: {props.price}</h5>
            </form>
        </div>
    )
}

export default Form
