import React from 'react'
import { useState } from 'react';



const Product = (props) => {

    const [quantity, setQuantity] = useState(0);


    const addOne = (count) => {
        setQuantity(count + 1);
    }
    const subOne = (count) => {
        if (count - 1 > 0) {
          setQuantity(count - 1);
        }
      }
    return (
        <div>
            <div className="menu">
                {props.meals.map((meal) => {
                    return (
                        <div className="meal" key={meal.id}>
                            <h3>{meal.name}</h3>
                            <p>{meal.price}</p>
                            <p>miqdar: {quantity} <span onClick={() => addOne(quantity)}>+</span> <span onClick={() => subOne(quantity)}>-</span></p>
                        </div>
                    )
                })}
            </div>
        </div>
    )
}

export default Product
