import React from 'react';
import { useParams } from 'react-router';
import Axios from 'axios';
import { useState, useEffect } from 'react';

const OrderDetail = () => {
    let { id } = useParams();
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        const fetchOrders = async () => {
            const responseOrders = await Axios.get("http://localhost:8000/orders").then((res) => {
                setOrders(res.data);
            })
                .catch((err) => console.log(err))
        }
        fetchOrders();
    }, []);
    return (
        <div className='orderDetail'>
            {orders.map((order) => {
            if ((order["id"]) == id) {
                return (
                    <table>
                        <tr>
                        <th>Məhsul Adı</th>
                        <th>Miqdar</th>
                        <th>Məbləğ</th>
                        <th>Status</th>
                        </tr>
                        <tbody>
                        {order["orders"].meals.map((meal) => {
                            return (
                                <tr>
                                    <td>{meal.name}</td>
                                    <td>{meal.amount}</td>
                                    <td>{parseFloat(meal.cost.toFixed(2))}</td>
                                    <td>{meal.status}</td>
                                </tr>
                            )
                        })}
                        </tbody>
                        {/* <h3>Order: {order["orders"].table}</h3> */}
                        {/* <p>id: {order["id"]}</p> */}
                    </table>
                )
            }
        })}
        </div>
        // <div>
        //     <h1>Order: {orders[id-1]["orders"].table}</h1>
        // </div>
    )
}

export default OrderDetail
