import React from 'react'
import { useEffect, useState } from 'react';

const Form = (props) => {
    const [table, setTable] = useState(null);
    const [server, setServer] = useState(null);
    const [choosenMeals, setChoosenMeals] = useState([]);
    const [quantity, setQuantity] = useState(0);
    const [order, setOrder] = useState({});
    const [price, setPrice] = useState(0);
    const [cost, setCost] = useState(null);

    const startOrder = (e) => {
        e.preventDefault();

    }

    const identifyMeal=(e)=>{
        props.meals.forEach((meal)=>{
            if(meal.name === e.target.value){
                setCost(meal.price);
            }
        })
    }

    const calculateCost=(e)=>{
        setCost(price )
    }
    return (
        <div>
            <form>
                <label htmlFor="table">Masa</label>
                <select name="table" id="table" onChange={(e) => setTable(e.target.value)}>
                    <option value="Sec" selected disabled>Sec</option>
                    {props.tables.map((table) => {
                        return (
                            <option key={table.id} value={table.id}>{table.id}</option>
                        )
                    })}
                </select>

                <label htmlFor="server">Xidmetci</label>
                <select name="server" id="server" onChange={(e) => setServer(e.target.value)}>
                    <option value="Sec" selected disabled>Sec</option>
                    {props.servers.map((server) => {
                        return (
                            <option key={server.id} value={server.name + " " + server.surname}>{server.name + " " + server.surname}</option>
                        )
                    })}
                </select>
                <button onClick={startOrder}>Sifarisi baslat</button>

                <label htmlFor="meal">Mehsul Adi</label>
                <select name="meal" id="meal" onChange={identifyMeal}>
                    <option value="Sec" disabled selected>Sec</option>
                    {props.meals.map((meal) => {
                        return (
                            <option value={meal.name} key={meal.id}>{meal.name}</option>
                        )
                    })}
                </select>
                <label htmlFor="amount">Miqdar</label>
                <input type="number" id='amount' name='amount' min="0" onChange={calculateCost} />
                <p>Qiymet: {cost}</p>
            </form>
        </div >
    )
}

export default Form
