import React, {useEffect, useState } from 'react'
import Axios from 'axios';
//import Form from './Form';
// import dataMenu from './data/menu.json';

const App = () => {
 const [meals, setMeals] = useState([]);
 const [orders, setOrders] = useState([]);
 const [tables, setTables] = useState([]);
 const [servers, setServers] = useState([]);
 const [price,setPrice] = useState(0);


 const [name,setName] = useState("");




 useEffect(()=>{
    const fetchMenu = async () => {
        const response = await Axios.get("http://localhost:8000/meals").then((res)=>{
            setMeals(res.data);
        })
        .catch((err)=>{
            console.log(err);
        });
        // const mealsList = await response.json();
        // console.log(mealsList);
        // setMeals(mealsList);
        // console.log(meals)
     };

    fetchMenu();
    console.log(orders);
 },[]);


 const addmeal=(e)=>{
    e.preventDefault();
    Axios.post("http://localhost:8000/meals",{
        name: name,

    })
 }
  return (
    <div>

        <h1>My menu</h1>
        <div className="menu">
            {meals.map((meal)=>{
                return(
                    <div className="meal" key={meal.id}>
                        <h3>name : {meal.name}</h3>
                        <p>price : {meal.price}</p>
                    </div>
                )
            })}
        </div>


        <form onSubmit={addmeal}>
            <label htmlFor="name" >name</label>
            <input type="text" id='name' name='name' onChange={(e)=> setName(e.target.value)} />
            <label htmlFor="price">price</label>
            <input type="number" id='price' name='price' onChange={(e)=> setPrice(e.target.value)} />
            <input type="submit" value="submit" />
        </form>


        {/* create order */}

    </div>
  )
}

export default App
